(window.webpackJsonp=window.webpackJsonp||[]).push([[8],{"4xjA":function(n,e,o){"use strict";o.r(e),e.default="# React\u52c9\u5f37\u4f1a #1 - \u3086\u308b\u3075\u308f\u8d85\u5165\u9580\u7de8 -\n\n## \u4e8b\u524d\u6e96\u5099\u3068\u57fa\u790e\u77e5\u8b58\n\n- `Node.js`\u304c\u30a4\u30f3\u30b9\u30c8\u30fc\u30eb\u3055\u308c\u3066\u3044\u308b\n  - [anyenv](https://github.com/anyenv/anyenv)\u304b\u3089\u306e`nodenv` or [nodenv](https://github.com/nodenv/nodenv)\n  - [Docker](https://github.com/otsukayuhi/docker_nodejs)\u3067\u4eee\u60f3\u5316\uff08\u5927\u585a\u4f5c\uff09\n- ES2015+\u306e\u77e5\u8b58\n  - \u5b9a\u6570\u3068\u5909\u6570\n  - \u30c6\u30f3\u30d7\u30ec\u30fc\u30c8\u30ea\u30c6\u30e9\u30eb\n  - \u5206\u5272\u4ee3\u5165\n  - \u30a2\u30ed\u30fc\u95a2\u6570\n  - map\n  - \u30b9\u30d7\u30ec\u30c3\u30c9\u69cb\u6587\n  - ES Modules\n\n### \u5b9a\u6570\u3068\u5909\u6570\n- \u5b9a\u6570 `const`\n  - \u30d6\u30ed\u30c3\u30af\u30b9\u30b3\u30fc\u30d7\u306e\u518d\u4ee3\u5165\u4e0d\u53ef\u306a\u5b9a\u6570\n\n```js\n{\n  const text = '\u30c6\u30ad\u30b9\u30c8';\n  console.log(text); // -> \u30c6\u30ad\u30b9\u30c8\n\n  text = '\u9055\u3046\u30c6\u30ad\u30b9\u30c8'; // -> Error: \"text\" is read-only\n}\n\nconsole.log(text); // -> ReferenceError: text is not defined\n```\n\n- \u5909\u6570 `let`\n  - \u30d6\u30ed\u30c3\u30af\u30b9\u30b3\u30fc\u30d7\u306e\u518d\u4ee3\u5165\u53ef\u80fd\u306a\u5909\u6570\n\n```js\n{\n  let num = 0;\n  console.log(num); // -> 0\n\n  num = 1;\n  console.log(num); // -> 1\n}\n\nconsole.log(num); // ReferenceError: num is not defined\n```\n\n- \u5909\u6570 `var`\n  - \u30d6\u30ed\u30c3\u30af\u30b9\u30b3\u30fc\u30d7\u3067\u306f\u306a\u3044\u5909\u6570\n  - `const`\u3068`let`\u3092\u4f7f\u3063\u3066\u3044\u304d\u307e\u3057\u3087\u3046 \n\n```js\n{\n  var text = '\u30c6\u30ad\u30b9\u30c8';\n}\n\nconsole.log(text); // -> \u30c6\u30ad\u30b9\u30c8\n```\n\n### \u30c6\u30f3\u30d7\u30ec\u30fc\u30c8\u30ea\u30c6\u30e9\u30eb\n\nES5\u306e\u66f8\u304d\u65b9\n\n```js\nconst age = '11\u6b73';\nconsole.log('\u79c1\u306e\u5e74\u9f62\u306f' + age + '\u3067\u3059\uff01');\n// -> \u79c1\u306e\u5e74\u9f62\u306f11\u6b73\u3067\u3059\uff01\n```\n\n\u30c6\u30f3\u30d7\u30ec\u30fc\u30c8\u30ea\u30c6\u30e9\u30eb\n\n```js\nconst age = '11\u6b73';\nconsole.log(`\u79c1\u306e\u5e74\u9f62\u306f${age}\u3067\u3059\uff01`);\n// -> \u79c1\u306e\u5e74\u9f62\u306f11\u6b73\u3067\u3059\uff01\n```\n\n### \u5206\u5272\u4ee3\u5165\n\n\u914d\u5217\n\n```js\nconst member = ['\u30cd\u30ba\u30df', '\u725b', '\u30c8\u30e9', '\u3046\u3055\u304e'];\nconst [a, b, c, d] = member;\n\nconsole.log(a); // -> \u30cd\u30ba\u30df\nconsole.log(c); // -> \u30c8\u30e9\n```\n\n\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\n\n```js\nconst person = {\n  name: '\u5927\u585a',\n  age: 3\n};\n\nconst {name, age} = person;\n\nconsole.log(name); // -> \u5927\u585a\nconsole.log(age); // -> 3\n```\n\n### \u30a2\u30ed\u30fc\u95a2\u6570\n\n\u95a2\u6570\u5ba3\u8a00\n\n```js\nfunction foo(a, b) {\n  return a + b;\n};\n\nconsole.log(foo(1, 2)); // -> 3\n```\n\n\u30a2\u30ed\u30fc\u95a2\u6570\n\n```js\nconst bar = (a, b) => {\n  return a + b;\n};\n\nconsole.log(bar(1, 2)); // -> 3\n```\n\n\u30a2\u30ed\u30fc\u95a2\u6570\u306e\u7701\u7565\n\n```js\n// \u5f15\u6570\u304c1\u3064\u306e\u5834\u5408\u3001()\u3092\u7701\u7565\u3067\u304d\u308b\n// \u8fd4\u308a\u5024\u306e\u307f\u306e\u5834\u5408\u3001{}\u3068return\u3092\u7701\u7565\u3067\u304d\u308b\nconst text = age => `\u79c1\u306e\u5e74\u9f62\u306f${age}\u3067\u3059\uff01`;\n\nconsole.log(text('11\u6b73')); // -> \u79c1\u306e\u5e74\u9f62\u306f11\u6b73\u3067\u3059\uff01\n```\n\n\u5f15\u6570\u306e\u5206\u5272\u4ee3\u5165\n\n```js\n// \u30aa\u30d6\u30b8\u30a7\u30af\u30c8\u3092\u5f15\u6570\u306b\u53d6\u308b\u5834\u5408\u3001\u5206\u5272\u4ee3\u5165\u306e\u8a18\u6cd5\u304c\u4f7f\u3048\u308b\nconst text = ({name, age}) => `\u79c1\u306e\u540d\u524d\u306f${name}\u3001\u5e74\u9f62\u306f${age}\u6b73\u3067\u3059\uff01`;\n\nconst person = {\n  name: '\u5927\u585a',\n  age: 3\n};\n\nconsole.log(text(person)); // -> \u79c1\u306e\u540d\u524d\u306f\u5927\u585a\u3001\u5e74\u9f62\u306f3\u6b73\u3067\u3059\uff01\n```\n\n### map\n\n\u7b2c\u4e00\u5f15\u6570\u306ecallback\u95a2\u6570\u3092\u3001\u914d\u5217\u306e\u9806\u756a\u901a\u308a\u306b\u5404\u8981\u7d20\u306b\u5bfe\u3057\u30661\u5ea6\u305a\u3064\u547c\u3073\u51fa\u3057\u3001\u305d\u306e\u7d50\u679c\u304b\u3089\u65b0\u3057\u3044\u914d\u5217\u3092\u751f\u6210\u3059\u308b\u3002\n\n```js\nconst member = ['\u30cd\u30ba\u30df', '\u725b', '\u30c8\u30e9', '\u3046\u3055\u304e'];\n\nconst greeting = member.map((name) => {\n  // name\u306b\u306f'\u30cd\u30ba\u30df'\u3084'\u725b'\u304c\u5165\u3063\u3066\u3044\u308b\n  return `${name}\u3067\u3059\uff01`;\n});\n\nconsole.log(greeting);\n// -> ['\u30cd\u30ba\u30df\u3067\u3059\uff01', '\u725b\u3067\u3059\uff01', '\u30c8\u30e9\u3067\u3059\uff01', '\u3046\u3055\u304e\u3067\u3059\uff01']\n```\n\n### \u30b9\u30d7\u30ec\u30c3\u30c9\u69cb\u6587\n\n\u914d\u5217\n\n```js\nconst newMember = ['\u9ed2\u732b','\u4e09\u6bdb\u732b','\u8336\u30c8\u30e9\u732b'];\nconst member = ['\u30cd\u30ba\u30df', '\u725b', '\u30c8\u30e9', ...newMember];\n\nconsole.log(member);\n// -> ['\u30cd\u30ba\u30df', '\u725b', '\u30c8\u30e9', '\u9ed2\u732b','\u4e09\u6bdb\u732b','\u8336\u30c8\u30e9\u732b']\n```\n\n\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\n\n```js\nconst addProperty = {\n  country: 'Japan',\n  hobby: 'music'\n}\n\nconst person = {\n  name: '\u5927\u585a',\n  age: 3,\n  ...addProperty\n};\n\nconsole.log(person);\n// -> {name: '\u5927\u585a', age: 3, country: 'Japan', hobby: 'music'}\n```\n\n\u95a2\u6570\u306e\u53ef\u5909\u9577\u5f15\u6570\n\n```js\nconst greeting = (...member) => member.map(name => `\u79c1\u306f${name}\u3067\u3059\uff01`);\n\nconsole.log(greeting('\u30cd\u30ba\u30df', '\u725b', '\u30c8\u30e9', '\u3046\u3055\u304e'));\n// -> ['\u79c1\u306f\u30cd\u30ba\u30df\u3067\u3059\uff01', '\u79c1\u306f\u725b\u3067\u3059\uff01', '\u79c1\u306f\u30c8\u30e9\u3067\u3059\uff01', '\u79c1\u306f\u3046\u3055\u304e\u3067\u3059\uff01']\n```\n\n### ES Modules\n\n`export default` \u3067\u30c7\u30fc\u30bf\u3092\u3001\u30b0\u30ed\u30fc\u30d0\u30eb\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\u306b\u8ffd\u52a0\u3059\u308b\u3053\u3068\u306a\u304f\u3001\u4ed6\u306eJavaScript\u30d5\u30a1\u30a4\u30eb\u3078\u51fa\u529b\u3067\u304d\u307e\u3059\u3002  \n\n```js\n// foo.js\nexport default '\u30c6\u30ad\u30b9\u30c8';\n```\n\n```js\n// index.js\nimport foo from \"./foo\";\n\nconsole.log(foo); // \u30c6\u30ad\u30b9\u30c8\n```\n\n\u3053\u308c\u3067\u3082OK\u3002\n\n```js\n// foo.js\nconst text = '\u30c6\u30ad\u30b9\u30c8';\n\nexport default text;\n```\n\n```js\n// index.js\n// text\u3058\u3083\u306a\u304f\u3066\u3082OK\nimport foo from \"./foo\";\n\nconsole.log(foo); // \u30c6\u30ad\u30b9\u30c8\n```\n\n\u8907\u6570`export`\u3059\u308b\u5834\u5408\u3002\n\n```js\n// foo.js\n// \u6570\u5024\nexport const num = 1;\n\n// \u914d\u5217\nexport const member = ['\u30cd\u30ba\u30df', '\u725b'];\n\n// \u30aa\u30d6\u30b8\u30a7\u30af\u30c8\nexport const person = {\n  name: '\u5927\u585a',\n  age: 3\n};\n\n// \u95a2\u6570\nexport const foo = (a, b) => a + b;\n```\n\n```js\n// index.js\nimport { num, member, person, foo } from \"./foo\";\n\nconsole.log(num); // 1\nconsole.log(member); // ['\u30cd\u30ba\u30df', '\u725b']\nconsole.log(person); // { name: '\u5927\u585a', age: 3 }\nconsole.log(foo(1, 2)); // 3\n```\n\n`* as moduleName`\u3067\u3001\u307e\u3068\u3081\u308b\u3053\u3068\u3082\u3067\u304d\u308b\u3002\n\n```js\n// index.js\nimport * as fooModule from \"./foo\";\n\n// fooModule\u306b\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\u5f62\u5f0f\u3067\u683c\u7d0d\u3055\u308c\u3066\u3044\u308b\nconst { num, member, person, foo } = fooModule;\n\nconsole.log(num); // 1\nconsole.log(member); // ['\u30cd\u30ba\u30df', '\u725b']\nconsole.log(person); // { name: '\u5927\u585a', age: 3 }\nconsole.log(foo(1, 2)); // 3\n```\n\n```js\n// index.js\n// num \u3092 number \u306b\u5909\u3048\u3066\u3044\u308b\nimport { num as number } from \"./foo\";\n\nconsole.log(number); // 1\n```\n\n`export default`\u3068`export`\u306f\u4f75\u7528\u3067\u304d\u308b\u3002\n\n```js\n// foo.js\n// \u6570\u5024\nexport const num = 1;\n\n// \u914d\u5217\nexport const member = ['\u30cd\u30ba\u30df', '\u725b'];\n\n// \u30aa\u30d6\u30b8\u30a7\u30af\u30c8\nexport const person = {\n  name: '\u5927\u585a',\n  age: 3\n};\n\n// \u95a2\u6570 (export default)\nconst foo = (a, b) => a + b;\n\nexport default foo;\n```\n\n```js\n// index.js\nimport foo, { num, member, person } from \"./foo\";\n\nconsole.log(num); // 1\nconsole.log(member); // ['\u30cd\u30ba\u30df', '\u725b']\nconsole.log(person); // { name: '\u5927\u585a', age: 3 }\nconsole.log(foo(1, 2)); // 3\n```\n\n\u3053\u308c\u3082OK\u3002\n\n```js\n// index.js\nimport foo, * as fooModule from \"./foo\";\n\nconst { num, member, person } = fooModule;\n\nconsole.log(num); // 1\nconsole.log(member); // ['\u30cd\u30ba\u30df', '\u725b']\nconsole.log(person); // { name: '\u5927\u585a', age: 3 }\nconsole.log(foo(1, 2)); // 3\n```\n\n## React / Next.js\u3068\u306f\uff1f\n\n### React\u3068\u306f\uff1f\n\nReact\u3068\u306f\u3001Facebook\u304c\u4f5c\u3063\u305f\u30e6\u30fc\u30b6\u30fc\u30a4\u30f3\u30bf\u30fc\u30d5\u30a7\u30a4\u30b9\u3092\u69cb\u7bc9\u3059\u308b\u305f\u3081\u306eJavaScript\u30e9\u30a4\u30d6\u30e9\u30ea\u3002  \n\u5ba3\u8a00\u7684\u306aView\u3092\u30b3\u30f3\u30dd\u30fc\u30cd\u30f3\u30c8\u30d9\u30fc\u30b9\u3067\u4f5c\u308b\u3053\u3068\u304c\u3067\u304d\u308b\u3002\n\n\u516c\u5f0f\u30b5\u30a4\u30c8\uff1ahttps://ja.reactjs.org/\n\n### Next.js\n\nNext.js\u3068\u306f\u3001React\u3067\u30b5\u30fc\u30d0\u30fc\u30b5\u30a4\u30c9\u30ec\u30f3\u30c0\u30ea\u30f3\u30b0\u3092\u3059\u308b\u305f\u3081\u306e\u30d5\u30ec\u30fc\u30e0\u30ef\u30fc\u30af\u3002  \nVue.js\u3067\u8a00\u3046\u3068\u3053\u308d\u306e\u3001Nuxt.js\u3002\u7c21\u5358\u306b\u30eb\u30fc\u30c6\u30a3\u30f3\u30b0\u3067\u304d\u3066\u3001\u9759\u7684\u30b5\u30a4\u30c8\u306e\u66f8\u304d\u51fa\u3057\u3082\u3067\u304d\u308b\u3002\n\n\u516c\u5f0f\u30b5\u30a4\u30c8\uff1ahttps://nextjs.org/\n\n## Next.js\u3067Web\u30b5\u30a4\u30c8\u3092\u3064\u304f\u3063\u3066\u307f\u3088\u3046\uff01\n\n### Next.js\u306e\u4f7f\u3044\u65b9\n\n\u5fc5\u8981\u306a\u30d1\u30c3\u30b1\u30fc\u30b8\u3092\u30a4\u30f3\u30b9\u30c8\u30fc\u30eb\n\n- next\n- react\n- react-dom\n\n```console\n$ npm i next react react-dom\n```\n\n`package.json`\u306b`scripts`\u3092\u8ffd\u8a18\u3002\n\n```json\n{\n  \"scripts\": {\n    \"dev\": \"next\"\n  }\n}\n```\n\n`./pages`\u30c7\u30a3\u30ec\u30af\u30c8\u30ea\u306b`index.js`\u3092\u8ffd\u52a0\u3002\n\n```console\n$ mkdir pages\n$ touch pages/index.js\n```\n\n```javascript\n// pages/index.js\nexport default () => <h1>Hello, Next.js!</h1>\n```\n\n\u30ed\u30fc\u30ab\u30eb\u30b5\u30fc\u30d0\u30fc\u3092\u8d77\u52d5\u3002\n\n```console\n$ npm run dev\n```\n\n`http://localhost:3000`\u306b\u30a2\u30af\u30bb\u30b9\u3057\u3066\u3001`Hello, Next.js!`\u304c\u8868\u793a\u3055\u308c\u3066\u3044\u308c\u3070\u3001OK\uff01\n\n#### \u30c7\u30a3\u30ec\u30af\u30c8\u30ea\u69cb\u6210\n\n- `./pages` \u30eb\u30fc\u30c6\u30a3\u30f3\u30b0\u306e\u5bfe\u8c61\n- `./static` \u9759\u7684\u30d5\u30a1\u30a4\u30eb\u306e\u7f6e\u304d\u5834\u6240\n  - \u753b\u50cf\u30d5\u30a1\u30a4\u30eb\u3068\u304b\n\n\u30c7\u30a3\u30ec\u30af\u30c8\u30ea\u306e\u30eb\u30fc\u30eb\u304c\u6c7a\u307e\u3063\u3066\u3044\u308b\u306e\u306f\u3001\u3053\u308c\u304f\u3089\u3044\u3002\n\n### JSX\u306e\u66f8\u304d\u65b9\n\n`pages/index.js`\u3092\u3001\u7701\u7565\u5f62\u306a\u3057\u306e\u5f62\u306b\u5909\u66f4\u3002\n\n```javascript\nimport React from 'react' // Next.js\u3067\u306f\u7701\u7565\u53ef\u80fd\n\n// return\u3067JSX\u3092\u8fd4\u3059\u95a2\u6570\u3092\u30b3\u30f3\u30dd\u30fc\u30cd\u30f3\u30c8\u3068\u547c\u3076\nconst Index = () => {\n  return <h1>Hello, Next.js!</h1>\n}\n\n// ES Modules\n// \u672c\u6765\u306f import \u3055\u308c\u3066 react-dom \u304c\u30ec\u30f3\u30c0\u30ea\u30f3\u30b0\u3059\u308b\u304c\u3001Next.js\u3067\u306f\u96a0\u853d\u3055\u308c\u3066\u3044\u308b\nexport default Index\n```\n\nJSX\u3067\u5b50\u8981\u7d20\u3092\u4f7f\u3046\u3002\n\n```javascript\nimport React from 'react'\n\nconst Index = () => {\n  // ()\u3067\u62ec\u308a\u3001;\u306e\u81ea\u52d5\u633f\u5165\u306b\u5bfe\u5fdc\n  // return\u3067\u8fd4\u3059JSX\u306f\u5fc5\u305a1\u3064\u306e\u8981\u7d20\n  return (\n    <div>\n      <h1>Hello, Next.js!</h1>\n    </div>\n  )\n}\n\nexport default Index\n```\n\n`JSX`\u5185\u306f`{}`\u3067JavaScript\u3092\u4f7f\u3046\u3002\n\n```javascript\nimport React from 'react'\n\nconst Index = () => {\n  const text = 'Next.js!'\n  return (\n    <div>\n      {/* \u30b3\u30e1\u30f3\u30c8\u30a2\u30a6\u30c8 */}\n      <h1>{`Hello, ${text}`}</h1>\n    </div>\n  )\n}\n\nexport default Index\n```\n\n\u30b3\u30f3\u30dd\u30fc\u30cd\u30f3\u30c8\u3092JSX\u5185\u3067\u4f7f\u3046\u3002\n\n```javascript\nimport React from 'react'\n\n// \u898b\u51fa\u3057\u7528\u306e\u30b3\u30f3\u30dd\u30fc\u30cd\u30f3\u30c8\nconst Heading = props => {\n  // \u5c5e\u6027\u306e\u5024\u306f\u3001\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\u306e\u30d7\u30ed\u30d1\u30c6\u30a3\u3068\u3057\u3066\u6e21\u3055\u308c\u308b\n  return <h1>{props.text}</h1>\n}\n\nconst Index = () => {\n  const text = 'Next.js!'\n  return (\n    <div>\n      {\n        /**\n        * \u30b3\u30f3\u30dd\u30fc\u30cd\u30f3\u30c8\u306e\u5c5e\u6027\u3067\u30c6\u30ad\u30b9\u30c8\u3092\u6e21\u3059\n        * \u3053\u308c\u3092Props\uff08\u30d7\u30ed\u30c3\u30d7\u30b9\uff09\u3068\u547c\u3076\n        */\n       }\n      <Heading text={`Hello, ${text}`} />\n    </div>\n  )\n}\n\nexport default Index\n```\n\n\u5b50\u8981\u7d20\u3092\u6e21\u3059\u3002\n\n```javascript\nimport React from 'react'\n\n// Props\u306f\u30aa\u30d6\u30b8\u30a7\u30af\u30c8\u306a\u306e\u3067\u3001\u5206\u5272\u4ee3\u5165\u304c\u4f7f\u3048\u308b\nconst Heading = ({ children }) => {\n  // children\u3067\u5b50\u8981\u7d20\u3092\u53d7\u3051\u53d6\u308b\n  return <h1>{children}</h1>\n}\n\nconst Index = () => {\n  const text = 'Next.js!'\n\n  return (\n    <div>\n      {/* \u30b3\u30f3\u30dd\u30fc\u30cd\u30f3\u30c8\u306e\u5b50\u8981\u7d20\u3067span\u8981\u7d20\u3092\u6e21\u3059 */}\n      <Heading>\n        <span>{`Hello, ${text}`}</span>\n      </Heading>\n    </div>\n  )\n}\n\nexport default Index\n```\n\n`div`\u304c\u3044\u3089\u306d\u3048\u3068\u304d\u3002\n\n```javascript\nimport React from 'react'\n\nconst Heading = ({ children }) => {\n  return <h1>{children}</h1>\n}\n\nconst Index = () => {\n  const text = 'Next.js!'\n  // React.Fragment\u3092\u4f7f\u3046\u3068\u305d\u306e\u8981\u7d20\u306f\u30ec\u30f3\u30c0\u30ea\u30f3\u30b0\u3055\u308c\u306a\u3044\n  return (\n    <React.Fragment>\n      <Heading>\n        <span>{`Hello, ${text}`}</span>\n      </Heading>\n      <p>div\u3067\u30e9\u30c3\u30d7\u3057\u305f\u306a\u3044\u306d\u3093</p>\n    </React.Fragment>\n  )\n}\n\nexport default Index\n```\n\n`div`\u304c\u3044\u3089\u306d\u3048\u3068\u304d2\u3002\n\n```javascript\nimport React from 'react'\n\nconst Heading = ({ children }) => {\n  return <h1>{children}</h1>\n}\n\nconst Index = () => {\n  const text = 'Next.js!'\n  // <React.Fragment></React.Fragment>\u306f<></>\u3068\u3082\u66f8\u3051\u308b\n  return (\n    <>\n      <Heading>\n        <span>{`Hello, ${text}`}</span>\n      </Heading>\n      <p>div\u3067\u30e9\u30c3\u30d7\u3057\u305f\u306a\u3044\u306d\u3093</p>\n    </>\n  )\n}\n\nexport default Index\n```\n\n\u30d5\u30a1\u30a4\u30eb\u3092\u5206\u3051\u3088\u3046\uff01\n\n```javascript\n// components/Heading.js\n// {} \u3068 return \u3092\u7701\u7565\u3067\u304d\u308b\nconst Heading = ({ children }) => <h1>{children}</h1>\n\nexport default Heading\n```\n\n```javascript\n// pages/index.js\nimport React from 'react'\nimport Heading from '../components/Heading'\n\nconst Index = () => {\n  const text = 'Next.js!'\n  return (\n    <>\n      <Heading>\n        <span>{`Hello, ${text}`}</span>\n      </Heading>\n      <p>div\u3067\u30e9\u30c3\u30d7\u3057\u305f\u306a\u3044\u306d\u3093</p>\n    </>\n  )\n}\n\nexport default Index\n```\n\nmap\u3067\u8981\u7d20\u306e\u53cd\u5fa9\u51e6\u7406\u3002\n\n```javascript\n// pages/index.js\nimport React from 'react'\nimport Heading from '../components/Heading'\n\n// \u914d\u5217\nconst member = ['\u30cd\u30ba\u30df', '\u725b', '\u30c8\u30e9', '\u3046\u3055\u304e']\n\nconst Index = () => {\n  const text = 'Next.js!'\n  return (\n    <>\n      <Heading>\n        <span>{`Hello, ${text}`}</span>\n      </Heading>\n      <ul>\n        {/*\n          member\u914d\u5217\u306e\u8981\u7d20\u306e\u6570\u3060\u3051<li>\u3092\u4f5c\u6210\n          key\u306bindex\u3092\u6e21\u3057\u3066\u3044\u308b\uff08\u30db\u30f3\u30c8\u306findex\u4ee5\u5916\u3092\u6e21\u3057\u305f\u3044\uff09\n        */}\n        {member.map((name, index) => (\n          <li key={index}>{name}</li>\n        ))}\n      </ul>\n    </>\n  )\n}\n\nexport default Index\n```\n\n`Link`\u3067\u30eb\u30fc\u30c6\u30a3\u30f3\u30b0\nonClick\u3067\u30a4\u30d9\u30f3\u30c8\u767a\u706b\n(`getInitialProps`\u3067\u30c7\u30fc\u30bf\u53d6\u5f97)\n\n\n### Emotion\u3067CSS in JS\n\n**Emotion**\u3068\u306f\u3001JavaScript\u3067CSS\u30b9\u30bf\u30a4\u30eb\u3092\u8a18\u8ff0\u3059\u308b\u305f\u3081\u306b\u8a2d\u8a08\u3055\u308c\u305f\u30e9\u30a4\u30d6\u30e9\u30ea\u3002  \n\u5f8c\u767a\u30e9\u30a4\u30d6\u30e9\u30ea\u306e\u305f\u3081\u3001**styled-component**\u7b49\u306e\u826f\u3044\u3068\u3053\u53d6\u308a\u3092\u3057\u3066\u3044\u308b\u3002\n\n\u5fc5\u8981\u306a\u30d1\u30c3\u30b1\u30fc\u30b8\u3092\u30a4\u30f3\u30b9\u30c8\u30fc\u30eb\u3057\u307e\u3057\u3087\u3002\n\n- @emotion/styled\n- @emotion/core\n\n```console\n$ npm i @emotion/styled @emotion/core\n```\n\n### \u9759\u7684\u30d5\u30a1\u30a4\u30eb\u306e\u66f8\u304d\u51fa\u3057\n\n```console\n$ next build\n$ next export\n```"}}]);